import React, { useState } from 'react';
import { AppBar, Tabs, Tab, Toolbar, Typography } from '@mui/material';
import { Link, Route, Routes, BrowserRouter as Router } from 'react-router-dom';
import Dashboard from './components/Dashboard';
import Backtesting from './components/Backtesting';
import Reports from './components/Reports';
import Assets from './components/Assets';
import Indicators from './components/Indicators'; // New Indicators component
import Settings from './components/Settings';

const App = () => {
  const [assets, setAssets] = useState([]);
  const [indicators, setIndicators] = useState([]); // State for indicators

  return (
    <Router>
      <AppBar position="static" style={{ backgroundColor: '#2C2C2C' }}>
        <Toolbar>
          <Typography variant="h6" sx={{ flexGrow: 1 }}>
            Trading Dashboard
          </Typography>
          <Tabs value={0}>
            <Tab label="Dashboard" component={Link} to="/" />
            <Tab label="Backtesting" component={Link} to="/backtesting" />
            <Tab label="Reports" component={Link} to="/reports" />
            <Tab label="Assets" component={Link} to="/assets" />
            <Tab label="Indicators" component={Link} to="/indicators" /> {/* New tab */}
            <Tab label="Settings" component={Link} to="/settings" />
          </Tabs>
        </Toolbar>
      </AppBar>

      <Routes>
        <Route path="/" element={<Dashboard assets={assets} indicators={indicators} />} />
        <Route path="/assets" element={<Assets assets={assets} setAssets={setAssets} />} />
        <Route path="/indicators" element={<Indicators indicators={indicators} setIndicators={setIndicators} />} /> {/* New route */}
        <Route path="/backtesting" element={<Backtesting />} />
        <Route path="/reports" element={<Reports />} />
        <Route path="/settings" element={<Settings />} />
      </Routes>
    </Router>
  );
};

export default App;
