import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Container,
  Typography,
  Button,
  TextField,
  List,
  ListItem,
  ListItemText,
  IconButton,
  Paper,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
} from '@mui/material';
import { Delete, Edit } from '@mui/icons-material';

const Dashboard = () => {
  const [assets, setAssets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [newAssetName, setNewAssetName] = useState('');
  const [newAssetValue, setNewAssetValue] = useState('');
  const [editAsset, setEditAsset] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const assetsResponse = await axios.get(`${process.env.REACT_APP_API_URL}/assets`);
        setAssets(assetsResponse.data);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleAddAsset = async () => {
    if (newAssetName && newAssetValue) {
      try {
        const response = await axios.post(`${process.env.REACT_APP_API_URL}/assets`, {
          name: newAssetName,
          value: newAssetValue,
        });
        setAssets([...assets, response.data]);
        setNewAssetName('');
        setNewAssetValue('');
      } catch (err) {
        console.error("Error adding asset:", err);
      }
    }
  };

  const handleRemoveAsset = async (id) => {
    try {
      await axios.delete(`${process.env.REACT_APP_API_URL}/assets/${id}`);
      setAssets(assets.filter(asset => asset._id !== id));
    } catch (err) {
      console.error("Error removing asset:", err);
    }
  };

  const handleEditAsset = (asset) => {
    setEditAsset(asset);
    setNewAssetName(asset.name);
    setNewAssetValue(asset.value);
  };

  const handleUpdateAsset = async () => {
    if (editAsset && newAssetName && newAssetValue) {
      try {
        const response = await axios.put(`${process.env.REACT_APP_API_URL}/assets/${editAsset._id}`, {
          name: newAssetName,
          value: newAssetValue,
        });
        setAssets(assets.map(asset => asset._id === editAsset._id ? response.data : asset));
        setEditAsset(null);
        setNewAssetName('');
        setNewAssetValue('');
      } catch (err) {
        console.error("Error updating asset:", err);
      }
    }
  };

  const handleCancelEdit = () => {
    setEditAsset(null);
    setNewAssetName('');
    setNewAssetValue('');
  };

  if (loading) {
    return <Typography variant="h6">Loading data...</Typography>;
  }

  if (error) {
    return <Typography variant="h6" color="error">Error loading data: {error}</Typography>;
  }

  return (
    <Container>
      <Typography variant="h4" gutterBottom>
        Dashboard Overview
      </Typography>
      <Typography variant="body1" gutterBottom>
        Welcome to the Trading Confluence Dashboard
      </Typography>
      
      <Paper elevation={3} style={{ padding: '16px', marginBottom: '16px' }}>
        <Typography variant="h6">Add New Asset</Typography>
        <TextField
          label="Asset Name"
          value={newAssetName}
          onChange={(e) => setNewAssetName(e.target.value)}
          fullWidth
          margin="normal"
        />
        <TextField
          label="Asset Value"
          value={newAssetValue}
          onChange={(e) => setNewAssetValue(e.target.value)}
          type="number"
          fullWidth
          margin="normal"
        />
        <Button
          variant="contained"
          color="primary"
          onClick={editAsset ? handleUpdateAsset : handleAddAsset}
          style={{ marginRight: '8px' }}
        >
          {editAsset ? "Update Asset" : "Add Asset"}
        </Button>
        {editAsset && (
          <Button variant="outlined" color="secondary" onClick={handleCancelEdit}>
            Cancel
          </Button>
        )}
      </Paper>

      <Typography variant="h6">Assets</Typography>
      <List>
        {assets.length > 0 ? (
          assets.map(asset => (
            <ListItem key={asset._id} divider>
              <ListItemText primary={`${asset.name}: ${asset.value}`} />
              <IconButton onClick={() => handleEditAsset(asset)}>
                <Edit />
              </IconButton>
              <IconButton onClick={() => handleRemoveAsset(asset._id)}>
                <Delete />
              </IconButton>
            </ListItem>
          ))
        ) : (
          <Typography variant="body2">No assets available.</Typography>
        )}
      </List>
    </Container>
  );
};

export default Dashboard;
