// src/components/TradeList.js
import React, { useState, useEffect } from 'react';
import { TextField, Button, MenuItem, Modal, Box } from '@mui/material';
import axios from 'axios';

const TradeList = () => {
  const [trades, setTrades] = useState([]);
  const [newTrade, setNewTrade] = useState({ asset: '', price: '', quantity: '' });
  const [assets, setAssets] = useState([]);
  const [open, setOpen] = useState(false);

  useEffect(() => {
    fetchTrades();
    fetchAssets();
  }, []);

  const fetchTrades = async () => {
    try {
      const response = await axios.get('/api/trades');
      setTrades(response.data);
    } catch (error) {
      console.error('Error fetching trades:', error);
    }
  };

  const fetchAssets = async () => {
    try {
      const response = await axios.get('/api/assets');
      setAssets(response.data);
    } catch (error) {
      console.error('Error fetching assets:', error);
    }
  };

  const addTrade = async () => {
    try {
      const response = await axios.post('/api/trades', newTrade);
      setTrades([...trades, response.data]);
      setNewTrade({ asset: '', price: '', quantity: '' });
      setOpen(false);
    } catch (error) {
      console.error('Error adding trade:', error);
    }
  };

  return (
    <div>
      <h2>Trade List</h2>
      <Button variant="contained" color="primary" onClick={() => setOpen(true)}>
        Add New Trade
      </Button>

      <Modal open={open} onClose={() => setOpen(false)}>
        <Box sx={{ padding: '20px', backgroundColor: 'white', margin: '100px auto', width: '300px' }}>
          <h3>Log New Trade</h3>
          <TextField
            label="Asset"
            select
            value={newTrade.asset}
            onChange={(e) => setNewTrade({ ...newTrade, asset: e.target.value })}
            fullWidth
            margin="normal"
          >
            {assets.map((asset) => (
              <MenuItem key={asset._id} value={asset._id}>
                {asset.name}
              </MenuItem>
            ))}
          </TextField>
          <TextField
            label="Price"
            value={newTrade.price}
            onChange={(e) => setNewTrade({ ...newTrade, price: e.target.value })}
            fullWidth
            margin="normal"
          />
          <TextField
            label="Quantity"
            value={newTrade.quantity}
            onChange={(e) => setNewTrade({ ...newTrade, quantity: e.target.value })}
            fullWidth
            margin="normal"
          />
          <Button variant="contained" color="primary" onClick={addTrade} fullWidth>
            Log Trade
          </Button>
        </Box>
      </Modal>

      <div>
        {trades.map((trade) => (
          <div key={trade._id}>
            <p>{trade.asset}: {trade.price} x {trade.quantity}</p>
            <Button variant="outlined" color="secondary" onClick={() => window.open(`https://www.tradingview.com/chart/?symbol=${trade.asset}`, '_blank')}>
              View on TradingView
            </Button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default TradeList;
