import React, { useState } from 'react';
import {
  Container,
  Typography,
  Grid,
  Paper,
  Button,
  Select,
  MenuItem,
} from '@mui/material';

const Dashboard = () => {
  const [asset, setAsset] = useState('');
  const [indicator1, setIndicator1] = useState('');
  const [indicator2, setIndicator2] = useState('');
  const [indicator3, setIndicator3] = useState('');

  const handleSubmit = () => {
    // Log the trade with the selected details
    console.log('Trade Logged:', { asset, indicator1, indicator2, indicator3 });
    // Reset fields after submission
    setAsset('');
    setIndicator1('');
    setIndicator2('');
    setIndicator3('');
  };

  return (
    <Container>
      <Typography variant="h4" gutterBottom>
        Dashboard
      </Typography>
      <Grid container spacing={3}>
        {/* Trade Logging Section */}
        <Grid item xs={12}>
          <Paper elevation={3} style={{ padding: '16px' }}>
            <Typography variant="h6" gutterBottom>
              Log a Trade
            </Typography>

            {/* Step 1: Select Asset */}
            <Typography>Select Asset:</Typography>
            <Select
              value={asset}
              onChange={(e) => setAsset(e.target.value)}
              fullWidth
              style={{ marginBottom: '16px' }}
            >
              <MenuItem value="Bitcoin">Bitcoin</MenuItem>
              <MenuItem value="Apple">Apple</MenuItem>
              {/* Add more assets as needed */}
            </Select>

            {/* Step 2: Select First Confluence Indicator */}
            <Typography>Select First Confluence Indicator:</Typography>
            <Select
              value={indicator1}
              onChange={(e) => setIndicator1(e.target.value)}
              fullWidth
              style={{ marginBottom: '16px' }}
            >
              <MenuItem value="Trend">Trend</MenuItem>
              <MenuItem value="RSI">RSI</MenuItem>
              {/* Add more indicators as needed */}
            </Select>

            {/* Step 3: Select Second Confluence Indicator */}
            <Typography>Select Second Confluence Indicator:</Typography>
            <Select
              value={indicator2}
              onChange={(e) => setIndicator2(e.target.value)}
              fullWidth
              style={{ marginBottom: '16px' }}
            >
              <MenuItem value="Moving Average">Moving Average</MenuItem>
              <MenuItem value="MACD">MACD</MenuItem>
              {/* Add more indicators as needed */}
            </Select>

            {/* Step 4: Select Third Confluence Indicator */}
            <Typography>Select Third Confluence Indicator:</Typography>
            <Select
              value={indicator3}
              onChange={(e) => setIndicator3(e.target.value)}
              fullWidth
              style={{ marginBottom: '16px' }}
            >
              <MenuItem value="Bollinger Bands">Bollinger Bands</MenuItem>
              <MenuItem value="Stochastic Oscillator">Stochastic Oscillator</MenuItem>
              {/* Add more indicators as needed */}
            </Select>

            {/* Submit Button */}
            <Button
              variant="contained"
              color="primary"
              onClick={handleSubmit}
              fullWidth
              style={{ marginTop: '16px' }}
            >
              Log Trade
            </Button>
          </Paper>
        </Grid>
      </Grid>
    </Container>
  );
};

export default Dashboard;
