import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Dashboard = () => {
  const [assets, setAssets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [newAssetName, setNewAssetName] = useState('');
  const [newAssetValue, setNewAssetValue] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const assetsResponse = await axios.get(`${process.env.REACT_APP_API_URL}/assets`);
        setAssets(assetsResponse.data);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleAddAsset = async () => {
    if (newAssetName && newAssetValue) {
      try {
        const response = await axios.post(`${process.env.REACT_APP_API_URL}/assets`, {
          name: newAssetName,
          value: newAssetValue,
        });
        setAssets([...assets, response.data]);
        setNewAssetName('');
        setNewAssetValue('');
      } catch (err) {
        console.error("Error adding asset:", err);
      }
    }
  };

  const handleRemoveAsset = async (id) => {
    try {
      await axios.delete(`${process.env.REACT_APP_API_URL}/assets/${id}`);
      setAssets(assets.filter(asset => asset._id !== id));
    } catch (err) {
      console.error("Error removing asset:", err);
    }
  };

  if (loading) {
    return <p>Loading data...</p>;
  }

  if (error) {
    return <p>Error loading data: {error}</p>;
  }

  return (
    <div>
      <h2>Dashboard Overview</h2>
      <p>Welcome to the Trading Confluence Dashboard</p>
      
      <h3>Add New Asset</h3>
      <input
        type="text"
        placeholder="Asset Name"
        value={newAssetName}
        onChange={(e) => setNewAssetName(e.target.value)}
      />
      <input
        type="number"
        placeholder="Asset Value"
        value={newAssetValue}
        onChange={(e) => setNewAssetValue(e.target.value)}
      />
      <button onClick={handleAddAsset}>Add Asset</button>
      
      <h3>Assets</h3>
      {assets.length > 0 ? (
        <ul>
          {assets.map(asset => (
            <li key={asset._id}>
              {asset.name}: {asset.value}
              <button onClick={() => handleRemoveAsset(asset._id)}>Remove</button>
            </li>
          ))}
        </ul>
      ) : (
        <p>No assets available.</p>
      )}
    </div>
  );
};

export default Dashboard;
