import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Dashboard = () => {
  const [assets, setAssets] = useState([]);
  const [trades, setTrades] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch assets data
        const assetsResponse = await axios.get('/api/assets');
        setAssets(assetsResponse.data);

        // Fetch trades data
        const tradesResponse = await axios.get('/api/trades');
        setTrades(tradesResponse.data);

        setLoading(false); // Data has loaded successfully
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return <p>Loading data...</p>;
  }

  if (error) {
    return <p>Error loading data: {error}</p>;
  }

  return (
    <div>
      <h2>Dashboard Overview</h2>
      <p>Welcome to the Trading Confluence Dashboard</p>
      
      <h3>Assets</h3>
      {assets.length > 0 ? (
        <ul>
          {assets.map(asset => (
            <li key={asset._id}>
              {asset.name}: {asset.value}
            </li>
          ))}
        </ul>
      ) : (
        <p>No assets available.</p>
      )}

      <h3>Recent Trades</h3>
      {trades.length > 0 ? (
        <ul>
          {trades.map(trade => (
            <li key={trade._id}>
              {trade.assetName} - {trade.tradeType} - {trade.amount}
            </li>
          ))}
        </ul>
      ) : (
        <p>No trades available.</p>
      )}
    </div>
  );
};

export default Dashboard;
