import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Container,
  Typography,
  Button,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableRow,
  IconButton,
  TextField,
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';

const Indicators = ({ indicators, setIndicators }) => {
  const [newIndicatorName, setNewIndicatorName] = useState('');

  // Fetch indicators from the backend when the component mounts
  useEffect(() => {
    axios.get('http://localhost:5000/indicators').then((response) => {
      setIndicators(response.data);
    });
  }, [setIndicators]);

  const handleAdd = () => {
    const newIndicator = {
      name: newIndicatorName,
    };

    axios.post('http://localhost:5000/indicators', newIndicator).then((response) => {
      setIndicators([...indicators, response.data]);
    });

    setNewIndicatorName('');
  };

  const handleRemove = (id) => {
    axios.delete(`http://localhost:5000/indicators/${id}`).then(() => {
      setIndicators(indicators.filter((indicator) => indicator._id !== id));
    });
  };

  return (
    <Container>
      <Typography variant="h4" gutterBottom>
        Manage Indicators
      </Typography>
      <Paper elevation={3} style={{ padding: '16px', marginBottom: '16px' }}>
        <Typography variant="h6">Indicators</Typography>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Indicator Name</TableCell>
              <TableCell>Actions</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {indicators.map((indicator, index) => (
              <TableRow key={index}>
                <TableCell>{indicator.name}</TableCell>
                <TableCell>
                  <IconButton color="error" onClick={() => handleRemove(indicator._id)}>
                    <DeleteIcon />
                  </IconButton>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
        <TextField
          label="New Indicator Name"
          fullWidth
          value={newIndicatorName}
          onChange={(e) => setNewIndicatorName(e.target.value)}
          style={{ marginTop: '16px' }}
        />
        <Button
          variant="contained"
          color="primary"
          style={{ marginTop: '16px' }}
          onClick={handleAdd}
        >
          Add Indicator
        </Button>
      </Paper>
    </Container>
  );
};

export default Indicators;
